library ieee;
use ieee.std_logic_1164.all;
use ieee.numeric_std.all;

library work;
use work.addr_defines.all;
use work.top_defines.all;

entity pcap_ctrl is
port (
    -- Clock and Reset
    clk_i               : in std_logic;
    reset_i             : in std_logic;
    sysbus_i            : in sysbus_t;
    posbus_i            : in posbus_t;
    -- Block Parameters
    ERR_STATUS       : in  std_logic_vector(31 downto 0);
    enable_o : out std_logic;
    frame_o : out std_logic;
    capture_o : out std_logic;
    -- Memory Bus Interface
    mem_cs_i            : in  std_logic;
    mem_wstb_i          : in  std_logic;
    mem_addr_i          : in  std_logic_vector(BLK_AW-1 downto 0);
    mem_dat_i           : in  std_logic_vector(31 downto 0);
    mem_dat_o           : out std_logic_vector(31 downto 0)
);
end pcap_ctrl;

architecture rtl of pcap_ctrl is

signal mem_addr : natural range 0 to (2**mem_addr_i'length - 1);

signal ENABLE      : std_logic_vector(31 downto 0);
signal ENABLE_WSTB : std_logic;
signal ENABLE_DLY      : std_logic_vector(31 downto 0);
signal ENABLE_DLY_WSTB : std_logic;
signal FRAME      : std_logic_vector(31 downto 0);
signal FRAME_WSTB : std_logic;
signal FRAME_DLY      : std_logic_vector(31 downto 0);
signal FRAME_DLY_WSTB : std_logic;
signal CAPTURE      : std_logic_vector(31 downto 0);
signal CAPTURE_WSTB : std_logic;
signal CAPTURE_DLY      : std_logic_vector(31 downto 0);
signal CAPTURE_DLY_WSTB : std_logic;

begin

mem_addr <= to_integer(unsigned(mem_addr_i));

--
-- Control System Interface
--
REG_WRITE : process(clk_i)
begin
    if rising_edge(clk_i) then
        if (reset_i = '1') then
            ENABLE <= (others => '0');
            ENABLE_WSTB <= '0';
            ENABLE_DLY <= (others => '0');
            ENABLE_DLY_WSTB <= '0';
            FRAME <= (others => '0');
            FRAME_WSTB <= '0';
            FRAME_DLY <= (others => '0');
            FRAME_DLY_WSTB <= '0';
            CAPTURE <= (others => '0');
            CAPTURE_WSTB <= '0';
            CAPTURE_DLY <= (others => '0');
            CAPTURE_DLY_WSTB <= '0';
        else
            ENABLE_WSTB <= '0';
            ENABLE_DLY_WSTB <= '0';
            FRAME_WSTB <= '0';
            FRAME_DLY_WSTB <= '0';
            CAPTURE_WSTB <= '0';
            CAPTURE_DLY_WSTB <= '0';

            if (mem_cs_i = '1' and mem_wstb_i = '1') then
                -- Input Select Control Registers
                if (mem_addr = PCAP_ENABLE) then
                    ENABLE <= mem_dat_i;
                    ENABLE_WSTB <= '1';
                end if;
                if (mem_addr = PCAP_ENABLE_DLY) then
                    ENABLE_DLY <= mem_dat_i;
                    ENABLE_DLY_WSTB <= '1';
                end if;
                if (mem_addr = PCAP_FRAME) then
                    FRAME <= mem_dat_i;
                    FRAME_WSTB <= '1';
                end if;
                if (mem_addr = PCAP_FRAME_DLY) then
                    FRAME_DLY <= mem_dat_i;
                    FRAME_DLY_WSTB <= '1';
                end if;
                if (mem_addr = PCAP_CAPTURE) then
                    CAPTURE <= mem_dat_i;
                    CAPTURE_WSTB <= '1';
                end if;
                if (mem_addr = PCAP_CAPTURE_DLY) then
                    CAPTURE_DLY <= mem_dat_i;
                    CAPTURE_DLY_WSTB <= '1';
                end if;

            end if;
        end if;
    end if;
end process;

--
-- Status Register Read
--
REG_READ : process(clk_i)
begin
    if rising_edge(clk_i) then
        if (reset_i = '1') then
            mem_dat_o <= (others => '0');
        else
            case (mem_addr) is
                when PCAP_ERR_STATUS =>
                    mem_dat_o <= ERR_STATUS;
                when others =>
                    mem_dat_o <= (others => '0');
            end case;
        end if;
    end if;
end process;

--
-- Instantiate Delay Blocks for System and Position Bus Fields
--
bitmux_ENABLE : entity work.bitmux
port map (
    clk_i       => clk_i,
    sysbus_i    => sysbus_i,
    bit_o       => enable_o,
    BITMUX_SEL  => ENABLE,
    BIT_DLY     => ENABLE_DLY
);

bitmux_FRAME : entity work.bitmux
port map (
    clk_i       => clk_i,
    sysbus_i    => sysbus_i,
    bit_o       => frame_o,
    BITMUX_SEL  => FRAME,
    BIT_DLY     => FRAME_DLY
);

bitmux_CAPTURE : entity work.bitmux
port map (
    clk_i       => clk_i,
    sysbus_i    => sysbus_i,
    bit_o       => capture_o,
    BITMUX_SEL  => CAPTURE,
    BIT_DLY     => CAPTURE_DLY
);




end rtl;